var link=document.querySelector(".search-link"),popup=document.querySelector(".search-wrapper"),form=document.querySelector(".search-form"),arrival=document.querySelector("#arrival"),leaving=document.querySelector("#leaving");link.addEventListener("click",function(e){e.preventDefault(),form.classList.toggle("search-form-show"),popup.classList.toggle("search-wrapper-show"),popup.classList.contains("search-form-error")&&popup.classList.remove("search-form-error")}),form.addEventListener("submit",function(e){arrival.value&&leaving.value||(e.preventDefault(),popup.classList.remove("search-form-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("search-form-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("search-form-show")&&(popup.classList.remove("search-wrapper-show"),form.classList.remove("search-form-show")))});
